UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `mqorot` = '<h3>נפתי -</h3><p>1. \"<q class=\"mfrj\"><strong>הנפתי</strong> הריח, כמניף בסודר בבית הבושם, להביא הריח מלמעלה למטה</q>\"<small class=\"small\"> (רש\"י)</small>. </p><p>2. \"<q class=\"mfrj\">התכתי</q>\", \"<q class=\"mfrj\">ענין הזלה והתכה, כמו \'<strong>נופת</strong> תטופנה\'</q>\"<small class=\"small\"> (מצודת דוד)</small>, \"<q class=\"mfrj\">הזלה ויציקה</q>\"<small class=\"small\"> (מלבי\"ם)</small>.</p><p>3. הקטרתי<small class=\"small\"> (רש\"י בשם דונש)</small>; ייתכן שהכוונה למכשיר נקוב כמו <strong>נָפָה</strong>, שבו היו שורפים עשבי בושם כדי לפזר את ריחם בחדר<small class=\"small\"> (דעת מקרא)</small>.</p>', `dquyot` = '<h3>נפתי -</h3><p>1. \"<q class=\"mfrj\"><strong>הנפתי</strong> הריח, כמניף בסודר בבית הבושם, להביא הריח מלמעלה למטה</q>\"<small class=\"small\"> (רש\"י)</small>. </p><p>2. \"<q class=\"mfrj\">התכתי</q>\", \"<q class=\"mfrj\">ענין הזלה והתכה, כמו \'<strong>נופת</strong> תטופנה\'</q>\"<small class=\"small\"> (מצודת דוד)</small>, \"<q class=\"mfrj\">הזלה ויציקה</q>\"<small class=\"small\"> (מלבי\"ם)</small>.</p><p>3. הקטרתי<small class=\"small\"> (רש\"י בשם דונש)</small>; ייתכן שהכוונה למכשיר נקוב כמו <strong>נָפָה</strong>, שבו היו שורפים עשבי בושם כדי לפזר את ריחם בחדר<small class=\"small\"> (דעת מקרא)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 7 AND `verse_number` = 17 AND 1 LIMIT 1;

