SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<br />', `hqblot` = '<p>הפסוק רומז לפרשת יוסף ואשת פוטיפר,&#160; <a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t0139.htm#11\">בראשית לט11</a>: \"<q class=\"psuq\">וַיְהִי כְּהַיּוֹם הַזֶּה, וַיָּבֹא הַבַּיְתָה לַעֲשׂוֹת מְלַאכְתּוֹ, וְאֵין<strong> אִישׁ</strong> מֵאַנְשֵׁי הַבַּיִת שָׁם <strong>בַּבָּיִת</strong></q>\"<small class=\"small\"> (ר\' אליהו שץ)</small>.\r\n		</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 7 AND `verse_number` = 19 AND 1 LIMIT 1;

