UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<p>\r\n<strong>ישט</strong> היא מילה דו-משמעית המתארת <a href=\"http://localhost/tnk1/kma/qjrim1/sta.html\">סטיה שהיא שטות</a>;\r\n&#160;&#160;<strong> אל ישט אל דרכיה לבך</strong>    = אל תעשה שטות ואל תסטה מדרך הישר בגללה.</p>\r\n   \r\n   \r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 7 AND `verse_number` = 25 AND 1 LIMIT 1;

