UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- <strong>לפעמים</strong> היא מסתובבת <strong>בחוץ</strong> (מחוץ לעיר או בשוק), <strong>ולפעמים</strong> היא מסתובבת <strong>ברחובות</strong>;</p><p> <strong>ובכל פינה</strong> שהיא מגיעה אליה היא <strong>אורבת</strong> לנערים תמימים כדי לפתותם -</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 7 AND `verse_number` = 12 AND 1 LIMIT 1;

