UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>וכשהיא ראתה את הנער חסר-הלב, <strong>היא החזיקה בו ונשקה לו</strong>; וכשראתה שהוא לא מתנגד, נעשתה חצופה <strong>ואמרה לו</strong> שֶקֶר <strong>בעזות פנים</strong> בלי למצמץ:</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 7 AND `verse_number` = 13 AND 1 LIMIT 1;

