UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>אחרי שיחה ממושכת, הנער <strong>פתאום</strong> בבת-אחת נכנע <strong>והולך אחריה</strong>;&#160;</p><p><strong>כ</strong>מו <strong>שור</strong>, חזק ובריא, שהולך <strong>ובא</strong> <strong>אל הטבח</strong> (השחיטה) - שום כוח חיצוני לא מכריח אותו, ובכל-זאת הוא לא מתנגד;</p><p>וכמו <strong>עכס</strong> (תכשיט), שלוקחים כדי <strong>לאסור</strong> ולקשור אותו אל רגלה של אשה - הוא מתנדנד הלוך ושוב, אך אינו מסוגל להתנתק ממנה - כך הולך אותו <strong>אויל</strong> אחרי האישה -</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 7 AND `verse_number` = 22 AND 1 LIMIT 1;

