UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>אחרי שיחה ממושכת, הנער <strong>פתאום</strong> בבת-אחת נכנע <strong>והולך אחריה</strong>;&#160;&#160;&#160; <strong>כ</strong>מו <strong>שור</strong>, חזק ובריא, שהולך <strong>ובא</strong> <strong>אל הטבח</strong> (השחיטה) מרצונו בלי שום כוח חיצוני שמכריח אותו; &#160;&#160; וכמו <strong>עכס</strong> (תכשיט), שלוקחים כדי <strong>לאסור</strong> ולקשור אותו אל רגלה של אשה, והוא מתנדנד הלוך ושוב אך אינו מסוגל להנתק ממנה; &#160;&#160; כך הולך אותו <strong>אויל</strong> אחרי הזונה -</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 7 AND `verse_number` = 22 AND 1 LIMIT 1;

