UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\'- הוא <strong>לקח בידו</strong> את <strong>צרור הכסף</strong> שלו כדי לעשות עסקים, <strong>ויבוא</strong> בחזרה אל <strong>ביתו</strong> רק לסעודת <strong>יום כיסוי</strong> הירח, בעוד המון זמן\'.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 7 AND `verse_number` = 20 AND 1 LIMIT 1;

