UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `hqblot` = '<p>בכל הפרק שלנו, האישה היא החזקה - היא מצליחה להטות ולהדיח את הנער כרצונה - הוא משתוקק אליה והיא מושלת בו. זאת בניגוד ל<a class=\"psuq\" href=\"/tnk1/prqim/t0103.htm#16\">בראשית ג16</a>: \"<q class=\"psuq\">וְאֶל אִישֵׁךְ תְּשׁוּקָתֵךְ, וְהוּא יִמְשָׁל בָּךְ</q>\" (<a class=\"hbdlym\" title=\"הבדלים\" href=\"/tnk1/messages/prqim_t0103_6.html\">חגי הופר</a>).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 7 AND `verse_number` = 21 AND 1 LIMIT 1;

