UPDATE `sgulot` SET `mqorot` = '<p>\r\n<strong>נתן קול</strong>      \r\n<a href=\"http://localhost/tnk1/kma/qjrim1/ntnqol.html\">= קרא בקול רם</a>. החכמה והתבונה לא מתביישות ולא מתנצלות, הן מפרסמות את המסרים שלהן כך שיגיעו לכל אוזן.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 1 AND 1 LIMIT 1;

