UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<p>מדוע בתחילת הפסוק מדובר על <strong>אמת</strong> ובסוף הפסוק על <strong>רשע</strong> (ולא על שקר)? </p><p>נראה שיש לקרוא את הפסוק הזה והפסוק הבא יחד, כתקבולת מוצלבת:</p><p><q class=\"psuq\">כִּי אֱמֶת יֶהְגֶּה חִכִּי</q> - כנגד - <q class=\"psuq\">אֵין בָּהֶם נִפְתָּל וְעִקֵּשׁ</q>;<br /><q class=\"psuq\">וְתוֹעֲבַת שְׂפָתַי רֶשַׁע</q> - כנגד - <q class=\"psuq\">בְּצֶדֶק כָּל אִמְרֵי פִי</q>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 7 AND 1 LIMIT 1;

