UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<p>למה <strong>כולם נכוחים</strong> דווקא <strong>למבין</strong>? &#160;&#160; כי \"<q class=\"mfrj\">אם ייפול אור האמת בליבם של הבלתי-מבינים, בוודאי שהופכים ללענה משפט, ובדעות הנבערות שלהם גורמין רעה לעצמן וגם לאחרים... הלא תראה, שהדבורים והנחשים כולם יונקים\r\n בשווה מפרח או מפרי, ואף-על-פי-כן, הדבורים מיניקתם עושים את הדבש, \r\nוהנחשים מן היניקה עצמה עושים את הארס הממית</q>\"<small class=\"small\"> (ע\"פ רמ\"ד ואלי)</small>.</p><p>ולמה כולם<strong> ישרים</strong> דווקא <strong>למוצאי דעת</strong>? &#160; \"<q class=\"mfrj\">כי הכל תלוי לפי \r\nמעשיהם של הלומדים, שאם הלימוד שלהם יביאם להתנהג במעשים הטובים, בוודאי \r\nשתשרה עליהם השראה של קדושה... אבל אם לא יטפל \r\nהמעשה טוב אל הלימוד, גם לא תשרה עליהם שום השראה של קדושה, וזה יגרום להם \r\nשכל הישרה יעקשו</q>\"<small class=\"small\"> (רמ\"ד ואלי)</small>.</p>', `dquyot` = '<p>למה <strong>כולם נכוחים</strong> דווקא <strong>למבין</strong>? &#160;&#160; כי \"<q class=\"mfrj\">אם ייפול אור האמת בליבם של הבלתי-מבינים, בוודאי שהופכים ללענה משפט... ובדעות הנבערות שלהם גורמין רעה לעצמן וגם לאחרים... <strong>למבין הם נכוחים</strong>, אבל אינם נכוחים אל הבלתי-מבין... הלא תראה, שהדבורים והנחשים כולם יונקים בשווה מפרח או מפרי, ואף-על-פי-כן, הדבורים מיניקתם עושים את הדבש, והנחשים מן היניקה עצמה עושים את הארס הממית</q>\"<small class=\"small\"> (רמ\"ד ואלי)</small>.</p><p>ולמה כולם<strong> ישרים</strong> דווקא <strong>למוצאי דעת</strong>? &#160; \"<q class=\"mfrj\">כי הכל תלוי לפי \r\nמעשיהם של הלומדים, שאם הלימוד שלהם יביאם להתנהג במעשים הטובים, בוודאי \r\nשתשרה עליהם השראה של קדושה, שלא תניחם לטעות בסברתם.. אבל אם לא יטפל \r\nהמעשה טוב אל הלימוד, גם לא תשרה עליהם שום השראה של קדושה, וזה יגרום להם \r\nשכל הישרה יעקשו... וזהו שאמר: <strong>וישרים למוצאי דעת</strong>, ואין דעת אלא השראה של קדושה המיישרת והמיישבת דעתו של אדם להבין את האמת על בוריו</q>\"<small class=\"small\"> (רמ\"ד ואלי)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 9 AND 1 LIMIT 1;

