UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<p>המילה <strong>נבחר</strong> יכולה להתפרש כ-</p><p>1. פועל בינוני - הנשוא של הצלע השניה: \"הדעת היא מובחרת יותר מהחרוץ\".</p><p>2. תואר - לוואי של המילה חרוץ: \"העדיפו לקחת דעת יותר מלקחת חרוץ מובחר\".</p>', `dquyot` = '<p>המילה <strong>נבחר</strong> יכולה להתפרש כ-</p><p>1. פועל בינוני - הנשוא של הצלע השניה: \"הדעת היא מובחרת יותר מהחרוץ\".</p><p>2. תואר - לוואי של המילה חרוץ: \"העדיפו לקחת דעת יותר מלקחת חרוץ מובחר\".</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 10 AND 1 LIMIT 1;

