UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<p>היתרון של החכמה על כסף וזהב נזכר כבר בפסוק 10. אולם כאן מדובר על ה<strong>פרי</strong> ועל ה<strong>תבואה</strong> של החכמה&#160;&#160; על-דרך הדרש, פרי הוא ניגוד של קליפה:</p><p>הפסוק הקודם על הקליפה, הרווח החיצוני והצדדי של החכמה, שהם <strong>העושר והכבוד</strong>;</p><p>הפסוק הזה מדבר על <strong>הפרי</strong>, הרווח העיקרי, שהוא עצם ידיעת החכמה, שהיא <strong>טובה מחרוץ ומפז</strong><small class=\"small\"> (ודומה לזה פירש רמ\"ד ואלי)</small>.</p>', `dquyot` = '<p>פריי</p><p>על-דרך הדרש, פרי הוא ניגוד של קליפה:</p><p>הפסוק הקודם דיבר על הקליפה, הרווח החיצוני והצדדי של החכמה, שהם <strong>העושר והכבוד</strong>;</p><p>הפסוק הזה מדבר על <strong>הפרי</strong>, הרווח העיקרי, שהוא עצם ידיעת החכמה, שהיא <strong>טובה מחרוץ ומפז</strong><small class=\"small\"> (ודומה לזה פירש רמ\"ד ואלי)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 19 AND 1 LIMIT 1;

