UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `mqorot` = '<h3>מהי ה<strong>ארץ </strong>ומהם ה<strong>חוצות</strong>?</h3><p> ניתן לפרש בשלוש רמות:</p><p>1. <strong>ארץ</strong> היא ארץ ישראל, ו<strong>חוצות</strong> הם שאר הארצות<small class=\"small\"> (רש\"י, רמ\"ד ואלי)</small>.</p><p>2. <strong>ארץ</strong> היא היבשה (ראו \"הקבלות\"), ו<strong>חוצות</strong> הם האיים הנפרדים מהיבשה, או המקומות שאינם ראויים ליישוב<small class=\"small\"> (מלבי\"ם)</small>.</p><p>3. <strong>ארץ</strong> היא כל כדור הארץ, ו<strong>חוצות</strong> הם כוכבים אחרים ברחבי היקום.</p>', `hqblot` = '<p>הפסוק רומז ליום השלישי לבריאת העולם,&#160;<a class=\"psuq\" href=\"/tnk1/prqim/t0101.htm#9\">בראשית א9-10</a>: \"<q class=\"psuq\">וַיֹּאמֶר\r\n אֱלֹהִים יִקָּווּ הַמַּיִם מִתַּחַת הַשָּׁמַיִם אֶל מָקוֹם אֶחָד \r\nוְתֵרָאֶה הַיַּבָּשָׁה וַיְהִי כֵן. וַיִּקְרָא אֱלֹהִים לַיַּבָּשָׁה <strong>\r\nאֶרֶץ</strong> וּלְמִקְוֵה הַמַּיִם קָרָא יַמִּים וַיַּרְא אֱלֹהִים כִּי טוֹב</q>\". ביום זה ה\' <strong>עשה</strong> את <strong>הארץ</strong> (היבשה), וכך התגלה לראשונה <strong>העפר </strong>&#160;<small class=\"small\"> (מלבי\"ם)</small>. </p><p>ראו גם: <a class=\"hbdlym\" title=\"הבדלים\" href=\"/tnk1/kma/qjrim1/hiqwut_hmym.html\">חוק הכלים השלובים והיקוות המים</a>.</p>', `dquyot` = '<h3>מהי ה<strong>ארץ </strong>ומהם ה<strong>חוצות</strong>?</h3><p> ניתן לפרש בשלוש רמות:</p><p>1. <strong>ארץ</strong> היא ארץ ישראל, ו<strong>חוצות</strong> הם שאר הארצות<small class=\"small\"> (רש\"י, רמ\"ד ואלי)</small>.</p><p>2. <strong>ארץ</strong> היא היבשה (ראו \"הקבלות\"), ו<strong>חוצות</strong> הם האיים הנפרדים מהיבשה, או המקומות שאינם ראויים ליישוב<small class=\"small\"> (מלבי\"ם)</small>.</p><p>3. <strong>ארץ</strong> היא כל כדור הארץ, ו<strong>חוצות</strong> הם כוכבים אחרים ברחבי היקום.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 26 AND 1 LIMIT 1;

