UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\"כשה\' <strong>אימץ</strong> (חיזק) את <strong>השחקים ממעל</strong> (האטמוספירה) וחיבר אותם אל הארץ;&#160;&#160; כשה\' <strong>עזז</strong> (חיזק) את <strong>המעיינות</strong> היוצאים <strong>מהתהום</strong> ונתן להם כוח לפרוץ מהאדמה ולהשקות את הארץ - אני הייתי כאן.\"</p>', `main_qod_subjects` = NULL, `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 28 AND 1 LIMIT 1;

