UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `mqorot` = '<h3>אל תפרעו -</h3><p>1. על-פי הפשט: אל תפרקו את עול המוסר (<strong>פרע מוסר</strong><a href=\"http://localhost/tnk1/kma/qjrim1/pra_musr.html\" class=\"mila mamr\"> = פרק את עול הביקורת, סירב לקבל ביקורת</a>).</p><p>2. ועל-דרך הדרש: אל תערבבו את הסדר: \"<q class=\"mfrj\">צריך האדם לקבל את <strong>המוסר</strong> הקודם אל <strong>החכמה</strong>... כי המוסר הוא כמו פתחו ומבוא אל החכמה.. ואין לעשות <strong>למפרע</strong>\r\n להקדים החכמה אל המוסר... שכבר אמרו חז\"ל: כל שיראת חטאו קודמת לחכמתו - \r\nחכמתו מתקיימת, וכל שאין יראת חטאו קודמת לחכמתו - אין חכמתו מתקיימת</q>\"<small class=\"small\"> (רמ\"ד ואלי)</small>.</p>', `dquyot` = '<h3>אל תפרעו -</h3><p>1. על-פי הפשט: אל תפרקו את עול המוסר (<strong>פרע מוסר</strong><a href=\"/tnk1/kma/qjrim1/pra_musr.html\" class=\"mila mamr\"> = פרק את עול הביקורת, סירב לקבל ביקורת</a>).</p><p>2. ועל-דרך הדרש: אל תערבבו את הסדר: \"<q class=\"mfrj\">צריך האדם לקבל את <strong>המוסר</strong> הקודם אל <strong>החכמה</strong>... כי המוסר הוא כמו פתחו ומבוא אל החכמה.. ואין לעשות <strong>למפרע</strong> להקדים החכמה אל המוסר... שכבר אמרו חז\"ל: כל שיראת חטאו קודמת לחכמתו - חכמתו מתקיימת, וכל שאין יראת חטאו קודמת לחכמתו - אין חכמתו מתקיימת</q>\"<small class=\"small\"> (רמ\"ד ואלי)</small>.</p><p><a href=\"/tnk1/kma/qjrim1/pra_musr.html\" class=\"mila mamr\"></a></p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 33 AND 1 LIMIT 1;

