UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<h3>מה פירוש הביטוי <strong>אהבו מוות</strong>?</h3><p>1. לפי רוב המפרשים, הכוונה: \"כאילו אהבו מוות\". מי שאינו לומד חכמה - פוגע בעצמו עד מוות.</p><p>2. וייתכן שהפסוק \r\nרומז ל\"יצר המוות\" בתורתו של פרויד - היצר הגורם לאדם לפגוע בעצמו או \r\nבאחרים. והפסוק מלמד שאדם בעל יצר המוות הוא אדם שונא חכמה - \r\nאנשים בעלי נטיות התאבדות, אינם פועלים על-פי צו התבונה <small class=\"small\">(חגי הופר)</small>.</p>', `dquyot` = '<h3>מה פירוש הביטוי <strong>אהבו מוות</strong>?</h3><p>1. לפי רוב המפרשים, הכוונה: \"כאילו אהבו מוות\". מי שאינו לומד חכמה - פוגע בעצמו עד מוות.</p><p>2. וייתכן שהפסוק \r\nרומז ל\"יצר המוות\" בתורתו של פרויד - היצר הגורם לאדם לפגוע בעצמו או \r\nבאחרים. והפסוק מלמד שאדם בעל יצר המוות הוא אדם שונא חכמה - \r\nאנשים בעלי נטיות התאבדות, אינם פועלים על-פי צו התבונה <small class=\"small\">(חגי הופר)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 36 AND 1 LIMIT 1;

