UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `mqorot` = '<p>המורה החכמה ממקדת את מאמצי השכנוע שלה ב\"קולות הצפים\" - <strong>הפתיים</strong> (<a href=\"http://localhost/tnk1/kma/qjrim1/pti.html\" class=\"hgdrh_kllyt dor1\">= המתפתים בקלות</a>) ו<strong>חסרי-הלב</strong> (<a href=\"http://localhost/tnk1/kma/qjrim1/xsrlv.html\" class=\"hgdrh\" title=\"חסר לב=\">= חסרי כשרון שכלי</a>).\r\n כרגע הם אמנם מתפתים לעשות מעשים רעים, אבל עם קצת מאמץ, אפשר לשכנע אותם \r\nלשנות כיוון. אולי בית-הספר היפה, והארוחות החמות שמגישים בו, ישכנעו אותם \r\nלעזוב את הרחוב ולחזור ללימודים.</p><p>ישנם טיפוסים, שהמורה החכמה כלל אינה מנסה להשפיע עליהם, כי אין סיכוי שהם ישתכנעו - <strong>הלץ והרשע</strong> (פסוקים 7-8); משאבי החינוך הם מוגבלים, ויש להשקיע אותם בצורה יעילה.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 9 AND `verse_number` = 4 AND 1 LIMIT 1;

