UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `mqorot` = '<h3><span><strong>עזבו פתאים וחיו</strong> - שלושה פירושים:</span></h3><p>1. המילה <strong>פתאים</strong> היא פניה - אתם, <strong>הפתאים</strong>, <strong>עזבו</strong> את דרככם <small class=\"small\">(מצודת דוד)</small>;&#160;&#160; אולם לפי זה חסר המושא של הפועל \"עזבו\".</p><p>2. המילה <strong>פתאים</strong> היא מושא - <strong>עזבו</strong> את דרך<strong> הפתיות</strong> <small class=\"small\">(רש\"י)</small>;&#160;&#160; אולם לפי זה היה ראוי להיות \"עזבו פתיות\".</p><p>3. המילה <strong>פתאים</strong> היא מושא, והכוונה \"<strong>עזבו</strong> את חבריכם <strong>הפתאים</strong>\" - עזבו את החברה הרעה, שמשפיעה עליכם לרעה, והתחילו <strong>לחיות</strong> חיים עצמאיים.</p><h3>ואשרו בדרך בינה</h3><p><strong>אשור</strong> <a href=\"http://localhost/tnk1/kma/qjrim1/ajur.html\" class=\"hgdrh_kllyt dor1\">= קרסול, ובהשאלה - צעד והליכה</a>.<strong>&#160;&#160; אִשְרוּ בדרך בינה</strong> = צעדו בדרך בינה; &#160;&#160; והשימוש בפועל זה רומז גם, שדרך זו מובילה לאושר.</p>', `dquyot` = '<h3><span><strong>עזבו פתאים וחיו</strong> - שלושה פירושים:</span></h3><p>1. המילה <strong>פתאים</strong> היא פניה - אתם, <strong>הפתאים</strong>, <strong>עזבו</strong> את דרככם <small class=\"small\">(מצודת דוד)</small>;&#160;&#160; אולם לפי זה חסר המושא של הפועל \"עזבו\".</p><p>2. המילה <strong>פתאים</strong> היא מושא - <strong>עזבו</strong> את דרך<strong> הפתיות</strong> <small class=\"small\">(רש\"י)</small>;&#160;&#160; אולם לפי זה היה ראוי להיות \"עזבו פתיות\".</p><p>3. המילה <strong>פתאים</strong> היא מושא, והכוונה \"<strong>עזבו</strong> את חבריכם <strong>הפתאים</strong>\" - עזבו את החברה הרעה, שמשפיעה עליכם לרעה, והתחילו <strong>לחיות</strong> חיים עצמאיים.</p><h3>ואשרו בדרך בינה</h3><p><strong>אשור</strong> <a href=\"/tnk1/kma/qjrim1/ajur.html\" class=\"hgdrh_kllyt dor1\">= קרסול, ובהשאלה - צעד והליכה</a>.<strong>&#160;&#160; אִשְרוּ בדרך בינה</strong> = צעדו בדרך בינה; &#160;&#160; והשימוש בפועל זה רומז גם, שדרך זו מובילה לאושר.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 9 AND `verse_number` = 6 AND 1 LIMIT 1;

