UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `hqblot` = '<h3>קולות צפים</h3><p>דבריה של <strong>אשת כסילות</strong> בפסוק 15 זהים לדבריה של גברת <strong>חכמות</strong> בפסוק 4. שתיהן פונות אל אותו קהל-יעד: הפתיים וחסרי-הלב.</p><p>החכמים הנבונים והצדיקים כבר תומכים ב<strong>חכמות</strong>;</p><p>הכסילים הלצים והרשעים כבר תומכים ב<strong>כסילות</strong>;</p><p>הפתיים וחסרי-הלב הם \"הקולות הצפים\", המתנדנדים בין החכמות לבין הכסילות. &#160; כמו בכל מערכת-בחירות, עיקר המאמץ ההסברתי משני הצדדים מופנה לקהל זה.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 9 AND `verse_number` = 16 AND 1 LIMIT 1;

