UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\"כשה\' <strong>שׂם</strong> (קבע) <strong>חוק לים</strong>, חוק הכלים השלובים הקובע שהמים לא יכולים <strong>לעבור</strong> את <strong>פי</strong> הים ולהציף את היבשה;&#160; כשה\' <strong>חוקק</strong> וקבע את גבולות <strong>הארץ</strong> (היבשה) -\"</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 29 AND 1 LIMIT 1;

