UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\"- אם ת<strong>תן </strong>דבר-חכמה ותוכחה <strong>לחכם</strong>, הוא יבקש לשמוע עוד כדי שיהיה <strong>עוד</strong> יותר <strong>חכם</strong>;&#160;&#160;&#160; ואם <strong>תודיע לצדיק</strong> דבר מצוה, הוא יבקש ממך <strong>להוסיף </strong>לו עוד מסר שיוכל <strong>לקחת</strong> וליישם\".</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 9 AND `verse_number` = 9 AND 1 LIMIT 1;

