UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p> <strong>יראת ה\'</strong> היא תנאי מקדים ל<strong>תחילת</strong> העיסוק ב<strong>חכמה</strong>;&#160;&#160; <strong>ודעת</strong> (אהבת) ה\', שהוא אלהים <strong>קדושים</strong>, היא תנאי מקדים לתחילת העיסוק ב<strong>בינה</strong>.</p><p>/ אדם <strong>מתחיל</strong> להיות <strong>חכם</strong> רק כשיש לו <strong>יראת ה\'</strong>;&#160;&#160; ומתחיל להיות <strong>נבון</strong> רק כשיש לו <strong>דעת</strong> על עניינים <strong>קדושים</strong> (מעל הטבע).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 9 AND `verse_number` = 10 AND 1 LIMIT 1;

