UPDATE `sgulot` SET `prefix` = NULL, `dquyot` = '<h3>מה זה <strong>קרת</strong>? </h3><p><strong>קרת</strong>, כמו <strong>קריה</strong>, היא עיר מוקפת חומה עם תקרה כמו מבצר גדול <small class=\"small\">(מלבי\"ם על ישעיהו א26. ורש\"י על פסוקנו פירש שהכוונה לתקרה מעל שער העיר)</small>. והיא שימשה כמרכז שלטוני.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 8 AND `verse_number` = 3 AND 1 LIMIT 1;

