UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<h3>למה לא נאמר <strong>תועה</strong> (בעצמו) אלא <strong>מתעה</strong> (את אחרים)?</h3><p>1. \"<q class=\"mfrj\">מתעה\r\n את היצר הטוב ומכריחו לילך בדרכי מיתה כנגד רצונו, כי הוא בא משורש החיים \r\nוחפץ להתקיים בדרכי חיים... וכשם שהצדיק השומר את התורה מחזיר את הרע לטוב \r\nושכרו גדול, כך הרשע העוזב את התורה מחזיר את הטוב לרע ועונשו גדול</q>\"<small class=\"small\"> (רמ\"ד ואלי פירוש ראשון)</small>.</p><p>2. \"<q class=\"mfrj\">מתעה\r\n את אחרים, כי יצר לב האדם רע מנעוריו, ולומדים יותר מן הרעים ממה שלומדים \r\nמן הטובים, ונמצא שחוטא אחד מעמיד כמה חוטאים אחרים שלומדים ממעשיו, והוא \r\nנענש על כולם</q>\"<small class=\"small\"> (רמ\"ד ואלי פירוש שני)</small>.</p>', `dquyot` = '<h3>למה לא נאמר <strong>תועה</strong> (בעצמו) אלא <strong>מתעה</strong> (את אחרים)?</h3><p>1. \"<q class=\"mfrj\">מתעה את היצר הטוב ומכריחו לילך בדרכי מיתה כנגד רצונו, כי הוא בא משורש החיים וחפץ להתקיים בדרכי חיים... וכשם שהצדיק השומר את התורה מחזיר את הרע לטוב ושכרו גדול, כך הרשע העוזב את התורה מחזיר את הטוב לרע ועונשו גדול</q>\"<small class=\"small\"> (רמ\"ד ואלי פירוש ראשון)</small>.</p><p>2. \"<q class=\"mfrj\">מתעה את אחרים, כי יצר לב האדם רע מנעוריו, ולומדים יותר מן הרעים ממה שלומדים מן הטובים, ונמצא שחוטא אחד מעמיד כמה חוטאים אחרים שלומדים ממעשיו, והוא נענש על כולם</q>\"<small class=\"small\"> (רמ\"ד ואלי פירוש שני)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 10 AND `verse_number` = 17 AND 1 LIMIT 1;

