UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `mqorot` = '<p>\"<q class=\"mfrj\"><strong>צדיק לעולם בל ימוט</strong> - כשהוא מט, אין מוטתו מוטת עולם, כי ייפול וקם</q>\" <small class=\"small\">(רש\"י, מצודות, מלבי\"ם, הגר\"א)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 10 AND `verse_number` = 30 AND 1 LIMIT 1;

