UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- שלמה אסף משלים כדי <strong>לדעת </strong>דברי <strong>חכמה ומוסר</strong> שאמרו חכמים אחרים, ומתוכם <strong>להבין </strong>ולפתח <strong>אמרי בינה</strong> חדשים בעצמו;</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 2 AND 1 LIMIT 1;

