UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>לקחת (ללמוד) <strong>מוסר</strong> מחוכם,&#160;&#160; <strong>צדק</strong> - היא עבודת האלהים, <strong>ומשפט</strong> - מה שבין אדם לחבירו, <strong>ומשרים</strong> - בדבר המידות והנהגת האדם.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tirgum` = '<p>- שלמה כתב ספר זה כדי <strong>לקחת</strong> בו את החכמה וה<strong>מוסר</strong> שה\' <strong>השכיל</strong> אותו בהם, ובעזרתם לעשות<strong> צדק</strong> ולנהל הליכים <strong>משפטיים</strong> תקינים <strong>וליישר</strong> את ההדורים בפשרה;</p><p>/ וכל הקורא בספר מוזמן <strong>לקחת</strong> ממנו <strong>מוסר </strong>(להפיק לקחים מעשיים), כך ש<strong>ישכיל</strong> (יצליח) בכל מעשיו ויפעל ב<strong>צדק ומשפט ומישרים</strong>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 3 AND 1 LIMIT 1;

