UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\r\n<strong>בן חכם</strong>  מקבל דברי \r\n<strong>מוסר</strong>  וביקורת \r\n<strong>מאביו</strong>, כי אביו רוצה שיהיה חכם עוד יותר;&#160;&#160;&#160; אולם בן \r\n<strong>לץ</strong> (הלועג לחכמה) <strong>לא שמע</strong>  מאביו כל \r\n<strong>גערה</strong>, כי אביו התייאש מלחנכו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 1 AND 1 LIMIT 1;

