UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p><strong>הנוצר</strong>  (משגיח) על \r\n<strong>פיו</strong>, שלא ידבר כשלא צריך, \r\n<strong>שומר</strong>  על <strong>נפשו</strong> (חייו) מצרות;&#160;&#160; אולם <strong>הפושק</strong>  (פותח בצורה מופקרת) את \r\n<strong>שפתיו</strong>, ומדבר כל העולה על רוחו בלי לחשוב, \r\n<strong>מחיתה</strong>  (שבירה, הריסה) תהיה \r\n<strong>לו</strong>.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 3 AND 1 LIMIT 1;

