UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- <strong>והשומע בקולי</strong> (אומרת החכמה) <strong>ישכון </strong>בעולם הזה <strong>בבטחון</strong>, גם אם הוא יחיד בדרכו;&#160;&#160; הוא יוכל להיות <strong>שאנן</strong> (שקט ורגוע), לא תבוא עליו <strong>רעה</strong> ש<strong>תפחיד</strong> אותו פתאום.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 33 AND 1 LIMIT 1;

