UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>(זהו סוף דברי החכמה, שאמרה:)&#160;&#160; אבל <strong>השומע לי, בטוח</strong> יהיה, <strong>ושאנן</strong> (שקט) שלא תבוא עליו <strong>פחד רעה</strong>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 33 AND 1 LIMIT 1;

