UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- שלמה כתב ספר זה כדי <strong>לקחת</strong> בו את החכמה וה<strong>מוסר</strong> שה\' <strong>השכיל</strong> אותו בהם, ובעזרתם לעשות<strong> צדק</strong> ולנהל הליכים <strong>משפטיים</strong> תקינים <strong>וליישר</strong> את היחסים בין אדם לחברו בפשרה;</p><p>/ וגם אתם, הקוראים בספר, מוזמנים <strong>לקחת</strong> ממנו <strong>מוסר </strong>(להפיק לקחים מעשיים) כדי <strong>להשכיל</strong> (להצליח) בכל מעשיכם, וכדי לדעת איך לעשות <strong>צדק ומשפט ומישרים</strong>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 3 AND 1 LIMIT 1;

