UPDATE `sgulot` SET `tirgum` = '<p><strong>בני</strong> (תלמידי היקר)! באת לבית-המדרש כדי ללמוד  דברים חדשים? יפה מאד! אבל עדיין, <strong>שמע</strong> והקשב לדברי <strong>המוסר</strong> שקיבלת במסורת <strong>מאביך</strong>, <strong>ואל תנטוש</strong> (תעזוב) את <strong>התורה</strong> <strong>שאמך</strong> לימדה אותך -</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 8 AND 1 LIMIT 1;

