UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>אם יאמרו \"לכה אתנו, &#160; נארבה</strong> (נשב במארב) לשפוך <strong>דם</strong>,&#160;&#160; <strong>נצפנה</strong> (נסתר) לארוב על דם <strong>נקי</strong>, לשפוך <strong>חנם</strong> על לא חמס בכפו\". &#160; (וכפל הדבר במילים שונות, כדרך המליצה ולהתמדת הפתוי).</p>\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 11 AND 1 LIMIT 1;

