UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>\"כמו השאול</strong> בולע כל הגוף, כן <strong>נבלע</strong> הכל כשהם ב<strong>חיים</strong> (בעודם בחוזק העושר), נבלע אותם כשהם <strong>תמימים</strong> (שלמים) במרבית ההון, <strong>וכמו היורד לבור</strong>, שיורד בו שלם בכל איבריו.\"</p><p> (והיא היא, וכפל הדבר במילים שונות).</p>\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<p>ניתן לפרש את הפסוק לפי <a href=\"http://localhost/tnk1/kma/qjrim1/tm.html\">שתי משמעויות שונות של התואר \"תמים\"</a>:</p><p>1. <strong>תמים</strong> = שלם בגופו: \"כמו שהקבר בולע את גופו של המת בשלמותו, כך אנחנו נבלע את קרבנותינו ואת רכושם בשלמותו\" \r\n<small>(ע\"פ רש\"י)</small>; כמו \"<q class=\"psuq\">עַד תֻּמָּם</q>\"<small class=\"small\"> (דברים ב15)</small>.&#160;&#160;&#160; זו טכניקת-פיתוי של נוכלים: הם מבטיחים לקרבנותיהם שיוכלו להשיג את <strong>כל</strong> מה שהם רוצים; בניגוד למציאות, שבה צריך תמיד לבחור ולהתפשר.</p><p>2. <strong>תמים</strong> = שלם במעשיו, צדיק, כמו נוח<small class=\"small\"> (בראשית ו9)</small>. ובדברי הנוכלים זה תואר של זלזול, כמו \'נאיבי\' בלשון ימינו: \"הם כל-כך צדיקים-תמימים, שהם לא מבינים שום דבר בענייני העולם הזה, ונוכל בקלות להטעות אותם ולהפיל אותם <strong>לבור</strong>\"<small class=\"small\"> (רמ\"ד ואלי)</small>. וכמו ב<a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t08b15.htm#11\">שמואל ב טו11</a>: \"<q class=\"psuq\">וְאֶת אַבְשָׁלוֹם הָלְכוּ מָאתַיִם אִישׁ מִירוּשָׁלַים קְרֻאִים וְהֹלְכִים <strong>לְתֻמָּם</strong>, וְלֹא יָדְעוּ כָּל דָּבָר</q>\".</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 12 AND 1 LIMIT 1;

