UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- <strong>\"נבלע</strong> את כל רכושם בבת-אחת,  <strong>כמו שהשאול</strong> (המוות) בולע את כל <strong>החיים</strong>!\",&#160;&#160; \"הם כל-כך <strong>תמימים </strong>שהם יפלו ברשתנו בקלות, כמו אנשים ההולכים <strong>לתומם ויורדים</strong> לתוך <strong>בור</strong>!\".</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 12 AND 1 LIMIT 1;

