UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>(ויוסיפו לומר לך:)&#160;&#160; \"אם תרצה, <strong>אתה תפיל</strong> את <strong>הגורל</strong> אשר תרצה, לברר לכל אחד חלקו;&#160;&#160;&#160; אם תרצה, <strong>יהיה כיס</strong> (אמתחת ושק)<strong> אחד לכלנו</strong> - נאכל ונשתה בשותפות מדמי הגזילה\".\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 14 AND 1 LIMIT 1;

