UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- שלמה החליט לפרסם את הספר כדי <strong>לתת</strong> <strong>ל</strong>אנשים ה<strong>מתפתים</strong> בקלות <strong>עורמה</strong> שתציל אותם מפיתוי מסוכן, &#160; ולתת <strong>ל</strong>כל <strong>נער</strong> <strong>דעת</strong> שתציל אותו מ<strong>מזימה</strong> מזיקה;</p><p>/ וגם אתם, הקוראים בספר, מוזמנים <strong>לתת </strong>מתוכו עצות&#160;<strong>לפתיים</strong> כך שיקבלו <strong>עורמה </strong>(זהירות מהטעיות), ולעזור <strong>ל</strong>כל <strong>נער</strong> ל<strong>דעת</strong> איך להיזהר מ<strong>מזימה</strong>.</p> \r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 4 AND 1 LIMIT 1;

