UPDATE `sgulot` SET `tirgum` = '<p>במקום להקשיב לחוטאים - הקשב נא למורות <strong>החכמות המרננות</strong> דברי-חכמה <strong>בחוץ</strong>, וחכמתן <strong>ברחובות </strong>מרימה את <strong>קולה</strong> -</p>' WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 20 AND 1 LIMIT 1;

