UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>-- ולכן, היזהרו מאנשי הדרך הרעה&#160;<strong>אשר אורחותיהם</strong> (מנהגיהם)<strong> עיקשים</strong>, אלימים ולא הוגנים;</p><p> והיזהרו מאנשי התהפוכות אשר כל<strong> מעגלותם</strong> (מנהגיהם) <strong>נלוזים</strong>, עקומים ולא צפויים.</p>', `mqorot` = '<p>הפסוק מתייחס שוב לשני סוגי האנשים הרעים:</p><p><strong>עיקש</strong>   \r\n<a href=\"http://localhost/tnk1/kma/qjrim1/iqj.html\">= עקום וקשה</a>; &#160; \r\n<strong>אשר אורחותיהם עיקשים</strong>   = האנשים הרעים הולכים בדרכים עקומות וקשות, אלימות ולא הוגנות.\r\n</p>\r\n  \r\n<p>\r\n<strong>נלוז</strong>   \r\n<a href=\"http://localhost/tnk1/kma/qjrim1/lz.html\">= עקום ונסתר</a>;&#160;&#160; \r\n<strong>ונלוזים במעגלותם</strong>   = אנשי התהפוכות הולכים בדרכים עקומות ונסתרות. </p><p>לסיכום: הנוכלים הולכים בדרכים עקומות ולא צפויות; אל תחשבו שאתם מבינים אותם או שאתם יכולים לחזות איך הם יתנהגו; פשוט תתרחקו מהם! \r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 15 AND 1 LIMIT 1;

