UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<p><strong>יסחו</strong>        - מהשורש <strong>נסח</strong> שמשמעו עקירה וגירוש, כמו <a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t0528.htm#63\">דברים כח63</a>: \"<q class=\"psuq\">...\r\n<strong>וְנִסַּחְתֶּם</strong> מֵעַל הָאֲדָמָה אֲשֶׁר אַתָּה בָא שָׁמָּה לְרִשְׁתָּהּ</q>\".</p><p>זוהי התוכנית של שלמה - לחלק נחלות לישרים ולתמימים, לעקור ולגרש את הרשעים והבוגדים.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 22 AND 1 LIMIT 1;

