UPDATE `sgulot` SET `mqorot` = '<h3>מה ההבדל בין <strong>הונך</strong> ל<strong>תבואתך</strong>?</h3><p>1. <strong>הון</strong>  \r\n  <a href=\"http://localhost/tnk1/kma/qjrim1/hon.html\" class=\"kll dor1\">= רכוש דומם, כסף זהב ובתים</a>; &#160; <strong>תבואה</strong> = רכוש מן הצומח, דגן תירוש ויצהר.&#160; כלומר יש לכבד את ה\' מכל סוגי הרכוש  \r\n<small>(ראו <a href=\"http://he.wikisource.org/wiki/%D7%A7%D7%98%D7%A2:%D7%9E%D7%A6%D7%95%D7%93%D7%95%D7%AA_%D7%A2%D7%9C_%D7%9E%D7%A9%D7%9C%D7%99_%D7%92_%D7%98\" class=\"external text\">מצודות</a>; ראו גם \"עצות\")</small>.</p><p>2. <strong>הון</strong> = הרכוש שכבר צברת, החסכונות;&#160;&#160; <strong>תבואה</strong> = הרכוש החדש, שאתה מביא מהשדה אל ביתך. כלומר יש לכבד את ה\' גם מהחסכונות וגם מההכנסה החודשית.</p>', `dquyot` = '<h3>מה ההבדל בין <strong>הונך</strong> ל<strong>תבואתך</strong>?</h3><p>1. <strong>הון</strong>  \r\n  <a href=\"/tnk1/kma/qjrim1/hon.html\" class=\"kll dor1\">= רכוש דומם, כסף זהב ובתים</a>; &#160; <strong>תבואה</strong> = רכוש מן הצומח, דגן תירוש ויצהר.&#160; כלומר יש לכבד את ה\' מכל סוגי הרכוש  \r\n<small>(ראו <a href=\"http://he.wikisource.org/wiki/%D7%A7%D7%98%D7%A2:%D7%9E%D7%A6%D7%95%D7%93%D7%95%D7%AA_%D7%A2%D7%9C_%D7%9E%D7%A9%D7%9C%D7%99_%D7%92_%D7%98\" class=\"external text\">מצודות</a>; ראו גם \"עצות\")</small>.</p><p>2. <strong>הון</strong> = הרכוש שכבר צברת, החסכונות;&#160;&#160; <strong>תבואה</strong> = הרכוש החדש, שאתה מביא מהשדה אל ביתך. כלומר יש לכבד את ה\' גם מהחסכונות וגם מההכנסה החודשית.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 9 AND 1 LIMIT 1;

