UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>אם תתכנן את מעשיך בחכמה, <strong>אז</strong> <strong>תלך</strong>     \r\n<strong>בבטחון</strong> ובשלוה<strong> בדרך </strong>להשגת מטרותיך; &#160;&#160; <strong>ורגלך</strong> <strong>לא</strong> <strong>תִגּוֹף</strong> (תתקל במכשולים), שהרי התכוננת מראש לכל בעיה אפשרית;</p>', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 23 AND 1 LIMIT 1;

