UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<h3>מה זה <strong>כסלך</strong>?</h3><p>1. <strong>כסלך</strong> מלשון כסילות, היפך חכמה. אם תתכנן את מעשיך בחכמה, ה\' יעזור לך בתחומים שבהם אתה <strong>כסיל</strong><small class=\"small\"> (רש\"י פירוש שני)</small>.</p><p>2. <strong>כסלך</strong> מעניין ביטחון ותקווה&#160;<small class=\"small\">(רש\"י פירוש ראשון, ומצודת ציון)</small>. אם תעסוק בתורה, ה\' יהיה הביטחון והתקווה שלך.</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 26 AND 1 LIMIT 1;

