UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p><strong>אל תאמר לרעך, </strong>שבא לקבל ממך דבר שאתה חייב לו, \"<strong>לך</strong> <strong>ושוב</strong> (תחזור מחר), <strong>ומחר</strong> <strong>אתן</strong> לך את מה שאני חייב לך\", כאשר <strong>יש לך</strong> להחזיר היום. החזרת חובות צריכה להיות בראש סדר הקדימויות!</p><p>/ ביקשו ממך טובה, <strong>ויש</strong> לך אפשרות <strong>לתת</strong> עכשיו? תן עכשיו, אל תדחה <strong>למחר</strong>! אולי מחר כבר לא יהיה לך?</p>\r\n', `mqorot` = '<p>כמו הפסוק הקודם, גם את הפסוק הזה אפשר לפרש על אדם שאנחנו חייבים לו משהו, או על סתם אדם שבא לבקש מאיתנו טובה.&#160;&#160;&#160; מובן שבמקרה הראשון, החובה להזדרז בתשלום החוב גדולה יותר.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 28 AND 1 LIMIT 1;

