UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- והשתמש <strong>בדעת</strong> כאשר קבע את היחס המדוייק שבין מים ליבשה: &#160;&#160; מי ה<strong>תהומות</strong> שכיסו את הארץ <strong>נבקעו</strong> והתייבשו כדי שהיבשה תתגלה;&#160;&#160; ומצד שני, ה<strong>שחקים</strong> (השמיים) <strong>רעפו</strong> (הטיפו) <strong>טַל</strong> כדי שהיבשה והצמחים שעליה ירוו מים.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 20 AND 1 LIMIT 1;

