UPDATE `sgulot` SET `mcudot` = '<p>- <strong>והיית כ</strong>אנשי הספינה ההולכת <strong>באמצע הים</strong> - כשהרוח חזק, דרכם להיות <strong>שוכבים</strong> בעבור חוזק תנועת הספינה;&nbsp;&nbsp; <strong>וכשוכב בראש חבל</strong> - הוא תורן הספינה, שכל החבלים תלוים בו; והשוכב שם, דעתו מבולבלת מפני ראיית הים ומהירות התנועה -</p>', `tirgum` = '<p>- כשתהיה שיכור, תדמיין שאתה <strong>כאילו</strong> יכול <strong>לשכב בלב</strong> (באמצע) <strong>הים</strong> בלי לטבוע, ויותר מזה - <strong>כאילו</strong> שאתה יכול <strong>לשכב</strong> על <strong>חיבל</strong> (תורן של ספינה) בלי ליפול -</p><p>/ - כשתהיה שיכור, תרגיש בחילה וטלטולים פנימיים <strong>כאילו</strong> אתה <strong>שוכב</strong> באמצע <strong>הים</strong>, ויותר מזה - <strong>כשוכב בראש חיבל</strong> (תורן של ספינה), המתנודד גם מהגלים וגם מהרוח -</p>', `mqorot` = NULL, `ecot` = NULL, `hqblot` = NULL, `dquyot` = NULL, `tosfot` = NULL, `full` = '<p><a href=\"/tnk1/ktuv/mj/23-33.html\">המאמר המקורי - כג33</a></p>' WHERE `book` = 'משל' AND `chapter_number` = 23 AND `verse_number` = 34 AND 1 LIMIT 1;

