UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>יתר</strong> - <strong>הצדיק</strong> יש לו <strong>יתרון</strong> ומעלה <strong>מרעהו</strong> שאינו צדיק; אבל <strong>דרך רשעים</strong> המצלחת היא <strong>מתעה</strong> את הרשעים לאחוז בדרכם, ולא יפנו אל היתרון ההוא.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 26 AND 1 LIMIT 1;

